import { useState, useEffect } from "react";
import axios from "axios";
import PersonForm from "./Components/PersonForm";

const Name = (props) => {
    return (
        <li>
            {props.name} - {props.number}
        </li>
    );
};

const Filter = (props) => {
    return (
        <div>
            filter shown with
            <input value={props.value} onChange={props.onChange} />
        </div>
    );
};

